sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var requestType = newValue;

   switch (requestType) {
      case 'legal':
          fillTheChoices([
            {value: 'legal1', label: 'Legal 1'},
            {value: 'legal2', label: 'Legal 2'},
            {value: 'legal3', label: 'Legal Outros'},
            {value: 'other', label: 'Other'}
         ]);
      break;

      case 'hr':
         fillTheChoices([
            {value: 'hr1', label: 'Recursos Humanos 1'},
            {value: 'hr2', label: 'Recursos Humanos 2'},
            {value: 'hr3', label: 'Recursos Humanos Outros'},
            {value: 'other', label: 'Other'}
         ]);
      break;

      case 'facilities':
       fillTheChoices([
            {value: 'facilities1', label: 'Facilities 1'},
            {value: 'facilities2', label: 'Facilities 2'},
            {value: 'facilities3', label: 'Facilities Other'},
            {value: 'other', label: 'Other'}
         ]);
      break;
   }

   function fillTheChoices(choices) {
      g_form.clearOptions('u_what_needed');

      for (var i=0; i < choices.length; i++) {
         g_form.addOption('u_what_needed', choices[i].value, choices[i].label);
      }
   }
}
