sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue === oldValue) {
      return;
   }

   //Type appropriate comment here, and begin script below
	var userEmail = new GlideAjax('GetEmailAddress');
	userEmail.addParam('sysparm_name', 'getEmail');
	userEmail.addParam('sysparm_userID', g_form.getValue('u_requested_for'));

	userEmail.getXML(emailField);

	function emailField(response) {
		var userEmail = response.responseXML.documentElement.getAttribute("answer");
		userEmail = JSON.parse(userEmail);

		g_form.clearValue();
		g_form.setValue('u_requested_for_email', userEmail.email);
	}
}
