sys_script_include.script:
var NeedItUilts = Class.create();
NeedItUilts.prototype = {
    initialize: function() {
		gs.info('thpreviateri: chamou');
    },

	isDatePast: function(strDate) {
		var agoraMesmo = new GlideDateTime();
		var whenSolicitado = new GlideDateTime(strDate);

		if (whenSolicitado.before(agoraMesmo)) {
			return true;
		} else {
			//return false;
			return this.isDateToday(strDate, agoraMesmo, whenSolicitado);
		}
	},

	isDateToday: function(strDate, agoraMesmo, whenSolicitado) {
		var hoje = agoraMesmo.getLocalDate();
		var isHoje = whenSolicitado.getLocalDate();

		if (hoje.compareTo(isHoje) == 0) {
			return true;
		}

		return false;
	},

    type: 'NeedItUilts'
};
